    def calculater(self,money):
        if money % 10 == 0:
            return money // 10, 0, 0, 0
        elif money % 5 == 0:
            return money // 10, (money % 10) // 5, 0, 0
        elif money < 5 and money % 2 == 0:
            return 0, 0, money // 2, 0
        elif money == 3:
            return 0, 0, 1, 1
        elif money == 1:
            return 0, 0, 0, 1
        else:
            ten = money // 10
            five = ((money) - ten * 10) // 5
            two = ((money) - (ten * 10) - five * 5) // 2
            one = ((money) - (ten * 10) - (five * 5) - two * 2)
            return ten, five, two, one

    def resultWindow(self):
        money = self.lineEdit.text()
        try:
            money = int(money)
        except:
            msg = QMessageBox()
            msg.setIcon(QMessageBox.Information)
            msg.setWindowTitle("Invalid amount")
            msg.setText("Please enter a valid amount")
            msg.exec_()
            return

        if money == 0:
            massage = QMessageBox()
            massage.setIcon(QMessageBox.Information)
            massage.setWindowTitle("Invalid amount")
            massage.setText("Please enter a valid amount")
            massage.exec_()
            return

        self.openResultWindow(money)

    def openResultWindow(self,money):
        self.NewDialog.show()
        ten,five,two,one=self.calculater(money)
        # print(ten,five,two,one)
        self.New_secren.tableWidget.item(0,0).setText(str(one))
        self.New_secren.tableWidget.item(1,0).setText(str(two))
