        self.New_secren.tableWidget.item(4,0).setText(str(one+two+five+ten))
        self.New_secren.tableWidget.item(0,1).setText(str(one*1))
        self.New_secren.tableWidget.item(1,1).setText(str(two*2))
        self.New_secren.tableWidget.item(2,1).setText(str(five*5))
        self.New_secren.tableWidget.item(3,1).setText(str(ten*10))
        self.New_secren.tableWidget.item(4,1).setText(str((one*1)+(two*2)+(five*5)+(ten*10)))

